mapper.delete:
DELETE FROM talks
WHERE talks_id = #{talks_id} AND
member_mem_id = #{mem_id}

mapper.select:
SELECT talks_id
FROM talks
WHERE talks_title = #{talks_title}
AND talks_content = #{talks_content}
AND member_mem_id = #{member_mem_id}

SELECT uuid, filename
FROM talks_image
WHERE talks_talks_id = #{talks_id}
